import React, { Component } from 'react';
import Home from './components/home/Home'
import './App.css';

class App extends Component {
    state = {
      fromStation: '',
      toStation: '',
      leavingDate: '',
      leavingTime: '',
      returnCheck: false,
      returnDate: '',
      returnTime: '',
      adults: 1,
      kids: 0,
      railcards: '',
    }

    handleChangeFrom = (e) => {
    this.setState({
      fromStation: e.target.value
    })
  }

  checkReturn = (e) => {
    this.setState({
      returnCheck: e.target.checked
    })
  }
  render() {

    const { fromStation, toStation, returnCheck } = this.state
    return (
      <div className="App">
      <Home 
        fromStationValue={fromStation}
        toStationValue={toStation} 
        returning={returnCheck} 
        checkReturn={this.checkReturn} 
        handleChangeFrom={this.handleChangeFrom}
      />

        {/* <HeaderBar/>

        <Menu/> */}
      </div>
    );
  }
}

export default App;
